import React from "react";
import classnames from "classnames";

/**
 * Used to render an application icon. Supply a suitable icon and
 * label, for example:
 *
 * ```js
 * <AppSwitcherItem>
 *   <AppSwitcherItemIcon>
 *     <Icon>icon_name</Icon>
 *   </AppSwitcherItemIcon>
 *   <AppSwitcherItemText>Application name</AppSwitcherItemText>
 * </AppSwitcherItem>
 * ```
 */
const AppSwitcherItem = ({ className, children, ...other }) => (
  <li
    className={classnames(
      "mdc-list-item",
      "cdk-global-header__app-switcher-apps-list-item",
      className
    )}
    role="menuitem"
  >
    <a className="cdk-global-header__app-switcher-apps-anchor" {...other}>
      {children}
    </a>
  </li>
);

export default AppSwitcherItem;
