import React from "react";
import { Avatar } from ".";
import { Icon } from "@cdk-uip/react-icon";
import { storiesOf } from "@storybook/react";
import { withKnobs, color, select } from "@storybook/addon-knobs";
import user from "./user.jpg";

storiesOf("avatar", module)
  .addDecorator(withKnobs)
  .add("props", () => {
    const type = select("Type", ["Empty", "Text", "Icon", "Image"], "Empty");

    return (
      <Avatar
        style={{
          backgroundColor: color("Custom background color", "#82c600"),
          color: color("Custom text color", "#fff")
        }}
      >
        {type === "Text" && "A"}
        {type === "Icon" && <Icon>favorite</Icon>}
        {type === "Image" && <img src={user} alt="user" />}
      </Avatar>
    );
  });
