import React from "react";
import { Avatar } from ".";
import { Icon } from "@cdk-uip/react-icon";
import renderer from "react-test-renderer";
import user from "./user.jpg";

it("renders empty", () => {
  expect(renderer.create(<Avatar />)).toMatchSnapshot();
});

it("renders with a custom class", () => {
  expect(renderer.create(<Avatar className="custom" />)).toMatchSnapshot();
});

it("renders with a custom style", () => {
  expect(
    renderer.create(
      <Avatar style={{ backgroundColor: "green", color: "white" }}>A</Avatar>
    )
  ).toMatchSnapshot();
});

it("renders with an icon", () => {
  expect(
    renderer.create(
      <Avatar style={{ backgroundColor: "green", color: "white" }}>
        <Icon>favorite</Icon>
      </Avatar>
    )
  ).toMatchSnapshot();
});

it("renders with an image", () => {
  expect(
    renderer.create(
      <Avatar>
        <img src={user} alt="User" />
      </Avatar>
    )
  ).toMatchSnapshot();
});
