import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * A bootstrap style badge component. Useful for counters and small
 * text blocks.
 */
const Badge = ({ className, disabled, children, ...other }) => (
  <span
    className={classnames(
      "cdk-badge",
      { "cdk-badge--disabled": disabled },
      className
    )}
    {...other}
  >
    {children}
  </span>
);

Badge.propTypes = {
  /** Disabled */
  disabled: bool
};

export default Badge;
