import React from "react";
import { Badge } from ".";
import { Body1, Display1 } from "@cdk-uip/react-typography";
import { storiesOf } from "@storybook/react";
import { withKnobs, text, boolean, select } from "@storybook/addon-knobs";

storiesOf("badge", module)
  .addDecorator(withKnobs)
  .add("props", () => {
    const container = select(
      "Sample container",
      ["None", "Body1", "Display1"],
      "None"
    );
    const badge = (
      <Badge disabled={boolean("Disabled", false)}>
        {text("Label", "Badge")}
      </Badge>
    );
    if (container === "Body1") {
      return <Body1>Body1 {badge}</Body1>;
    }
    if (container === "Display1") {
      return <Display1>Display1 {badge}</Display1>;
    }
    return badge;
  });
