import React from "react";
import { BottomSheet, BottomSheetTitle } from ".";
import { Button } from "@cdk-uip/react-button";
import { FAB } from "@cdk-uip/react-fab";
import { NavList, NavListItem, ListItemGraphic } from "@cdk-uip/react-list";
import { Icon } from "@cdk-uip/react-icon";
import { storiesOf } from "@storybook/react";

class Wrapper extends React.Component {
  state = { show: false };

  toggleSheet() {
    this.setState({ show: !this.state.show });
  }

  render() {
    return (
      <div>
        <Button onClick={() => this.toggleSheet()}>Show</Button>
        <BottomSheet open={this.state.show} onClose={() => this.toggleSheet()}>
          {this.props.children}
        </BottomSheet>
      </div>
    );
  }
}

