import React from "react";
import classnames from "classnames";

/**
 * You can use the `BottomSheetTitle` component to specify a title.
 */
const BottomSheetTitle = ({ className, children, ...other }) => (
  <h3 className={classnames("cdk-bottom-sheet--title", className)} {...other}>
    {children}
  </h3>
);

export default BottomSheetTitle;
