import React from "react";
import {
  Card,
  CardHeader,
  CardTitle,
  CardSubtitle,
  CardMedia,
  CardText,
  CardPrimaryAction,
  CardActions,
  CardActionButton,
  CardActionButtons,
  CardActionIcons,
  CardActionIcon,
  CardActionIconToggle
} from ".";
import { Avatar } from "@cdk-uip/react-avatar";
import { Icon } from "@cdk-uip/react-icon";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <Card>
        <CardPrimaryAction>
          <CardHeader>
            <CardTitle large>Large title</CardTitle>
            <CardSubtitle>Subtitle</CardSubtitle>
          </CardHeader>
          <CardMedia
            style={{
              backgroundImage: "image.png",
              backgroundSize: "cover",
              backgroundRepeat: "no-repeat",
              height: "10rem"
            }}
          />
          <CardText>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat.
          </CardText>
        </CardPrimaryAction>
        <CardActions>
          <CardActionButtons>
            <CardActionButton>Action 1</CardActionButton>
            <CardActionButton>Action 2</CardActionButton>
          </CardActionButtons>
          <CardActionIcons>
            <CardActionIconToggle iconOn="favorite" iconOff="favorite_border" />
            <CardActionIcon>share</CardActionIcon>
          </CardActionIcons>
        </CardActions>
        <CardActions fullBleed>
          <CardActionButton>
            Full bleed action
            <Icon>arrow_forward</Icon>
          </CardActionButton>
        </CardActions>
      </Card>
    )
  ).toMatchSnapshot();
});

it("renders with a header avatar", () => {
  expect(
    renderer.create(
      <Card>
        <CardHeader avatar>
          <Avatar>A</Avatar>
          <CardTitle>Large title</CardTitle>
          <CardSubtitle>Subtitle</CardSubtitle>
        </CardHeader>
        <CardText>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat.
        </CardText>
      </Card>
    )
  ).toMatchSnapshot();
});

it("renders 16x9", () => {
  expect(
    renderer.create(
      <Card>
        <CardMedia style={{ backgroundImage: "image.png" }} scale="16-9" />
      </Card>
    )
  ).toMatchSnapshot();
});

it("renders square", () => {
  expect(
    renderer.create(
      <Card>
        <CardMedia style={{ backgroundImage: "image.png" }} scale="square" />
      </Card>
    )
  ).toMatchSnapshot();
});
