import React from "react";
import classnames from "classnames";

/**
 * Used to wrap `<CardActionButton>` components when mixing buttons
 * and icons in the same set of actions.
 */
const CardActionButtons = ({ className, ...other }) => (
  <div
    className={classnames("mdc-card__action-buttons", className)}
    {...other}
  />
);

export default CardActionButtons;
