import React from "react";
import classnames from "classnames";
import { Icon } from "@cdk-uip/react-icon";

/**
 * Represents an icon action on a card.
 */
const CardActionIcon = ({ className, ...other }) => (
  <Icon
    className={classnames(
      "mdc-card__action",
      "mdc-card__action--icon",
      className
    )}
    {...other}
  />
);

export default CardActionIcon;
