import React from "react";
import classnames from "classnames";
import { IconToggle } from "@cdk-uip/react-icon-toggle";

/**
 * Represents an icon toggle action on a card. Wraps the
 * `<IconToggle>` component so supports the same list of props.
 *
 * Supply the two icon states using the `iconOn` and `iconOff` props.
 */
const CardActionIconToggle = ({ className, ...other }) => (
  <IconToggle
    className={classnames(
      "mdc-card__action",
      "mdc-card__action--icon",
      className
    )}
    {...other}
  />
);

export default CardActionIconToggle;
