import React from "react";
import classnames from "classnames";

/**
 * Used to wrap `<CardActionIcon>` and `<CardActionIconToggle>` components
 * when mixing buttons and icons in the same set of actions.
 */
const CardActionIcons = ({ className, ...other }) => (
  <div className={classnames("mdc-card__action-icons", className)} {...other} />
);

export default CardActionIcons;
