import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * Used as a container for `CardTitle` and `CardSubtitle` components.
 */
const CardHeader = ({ avatar, className, children, ...other }) => (
  <section
    className={classnames(
      "mdc-card__primary",
      { "mdc-card__primary--avatar": avatar },
      className
    )}
    {...other}
  >
    {children}
  </section>
);

CardHeader.propTypes = {
  /** Header contains an avatar */
  avatar: bool
};

export default CardHeader;
