import React from "react";
import classnames from "classnames";
import { oneOf } from "prop-types";

/**
 * Used to display images/videos.
 */
const CardMedia = ({ className, scale, children, ...other }) => (
  <div
    className={classnames(
      "mdc-card__media",
      { [`mdc-card__media--${scale}`]: scale },
      className
    )}
    {...other}
  >
    {children}
  </div>
);

CardMedia.propTypes = {
  /** Image scale */
  scale: oneOf(["square", "16-9"])
};

export default CardMedia;
