import React from "react";
import classnames from "classnames";

/**
 * An absolutely-positioned box the same size as the media area,
 * for displaying a title or icon on top of the background-image.
 */
const CardMediaContent = ({ className, ...other }) => (
  <div
    className={classnames("mdc-card__media-content", className)}
    {...other}
  />
);

export default CardMediaContent;
