import React from "react";
import classnames from "classnames";

/**
 * The main tappable area of the card. Typically contains most (or all) card
 * content except card actions. Only applicable to cards that have a
 * primary action that the main surface should trigger.
 */
const CardPrimaryAction = ({ className, ...other }) => (
  <div
    className={classnames("mdc-card__primary-action", className)}
    {...other}
  />
);

export default CardPrimaryAction;
