import React from "react";
import ReactDOM from "react-dom";
import TestUtils from "react-dom/test-utils";
import { Checkbox } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(renderer.create(<Checkbox checked />)).toMatchSnapshot();
});

it("supports indeterminate", () => {
  const div = document.createElement("div");
  const component = ReactDOM.render(<Checkbox indeterminate />, div);
  const input = TestUtils.findRenderedDOMComponentWithTag(component, "input");
  expect(input.indeterminate).toBe(true);
  // Re-rendering will fire componentDidUpdate
  ReactDOM.render(<Checkbox indeterminate={false} />, div);
  expect(input.indeterminate).toBe(false);
});
