import React from "react";
import classnames from "classnames";

/**
 * Use with the `Chip` `contact` prop to add contact text to a chip,
 * usually a single letter.
 */
const ChipContactText = ({ className, children, ...other }) => (
  <span className={classnames("cdk-chip__contact", className)} {...other}>
    {children}
  </span>
);

export default ChipContactText;
