import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * Official Material Design chip implementation.
 *
 * > You should always wrap `<Chip>` components in a `<ChipSet>`
 * component.
 */
const Chip = ({ className, selected, exit, ...other }) => (
  <div
    className={classnames(
      "mdc-chip",
      {
        "mdc-chip--selected": selected,
        "mdc-chip--exit": exit
      },
      className
    )}
    {...other}
  />
);

Chip.propTypes = {
  /** Indicates that the chip is selected. */
  selected: bool,
  /** Applies an exit animation to the chip. */
  exit: bool
};

export default Chip;
