import React from "react";
import ChipTrailingIcon from "./ChipTrailingIcon";
import { func } from "prop-types";

/**
 * The delete button used with **input chips**.
 */
const ChipDeleteIcon = ({ className, onClick }) => (
  <ChipTrailingIcon
    className={className}
    role="button"
    tabIndex="0"
    onClick={e => {
      e.stopPropagation();
      onClick(e);
    }}
  >
    cancel
  </ChipTrailingIcon>
);

ChipDeleteIcon.propTypes = {
  /** Called when the button is clicked. */
  onClick: func.isRequired
};

export default ChipDeleteIcon;
