import React from "react";
import classnames from "classnames";
import ChipIcon from "./ChipIcon";
import { bool } from "prop-types";

/**
 * used for leading icon in a chip.
 */
const ChipLeadingIcon = ({ className, hidden, ...other }) => (
  <ChipIcon
    className={classnames(
      "mdc-chip__icon--leading",
      { "mdc-chip__icon--leading-hidden": hidden },
      className
    )}
    {...other}
  />
);

ChipLeadingIcon.propTypes = {
  /** Hides the leading icon in a filter chip when the chip is selected. */
  hidden: bool
};

export default ChipLeadingIcon;
