import React from "react";
import classnames from "classnames";
import ChipIcon from "./ChipIcon";

/**
 * Represents a trailing icon which removes the chip. Only use with **input chips**.
 */
const ChipTrailingIcon = ({ className, ...other }) => (
  <ChipIcon
    className={classnames("mdc-chip__icon--trailing", className)}
    {...other}
  />
);

export default ChipTrailingIcon;
