import React from "react";
import classnames from "classnames";

/**
 * Circular progress bar.
 * Can be used with cards, type-ahead, lazy loading, etc.
 * Based on: http://materializecss.com/preloader.html and supported in IE11 and Edge.
 */
const CircularProgress = ({ className, ...other }) => (
  <div
    className={classnames(
      "cdk-circular-progress",
      "cdk-circular-progress--indeterminate",
      className
    )}
  >
    <div className="cdk-circular-progress--indeterminate__inner">
      <div className="cdk-circular-progress--indeterminate__left-half">
        <div className="cdk-circular-progress--circle cdk-circular-progress--indeterminate__circle--left" />
      </div>
      <div className="cdk-circular-progress--indeterminate__right-half">
        <div className="cdk-circular-progress--circle cdk-circular-progress--indeterminate__circle--right" />
      </div>
    </div>
  </div>
);

export default CircularProgress;
