import { currencyDecimalPlaces } from "./currency";

test("currencyDecimalPlaces", () => {
  expect(currencyDecimalPlaces("JPY")).toBe(0);
  expect(currencyDecimalPlaces("mga")).toBe(1);
  expect(currencyDecimalPlaces("GBP")).toBe(2);
  expect(currencyDecimalPlaces("TND")).toBe(3);
  expect(currencyDecimalPlaces("CLF")).toBe(4);
});
