import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * Data table based on Material Design Lite.
 *
 * https://getmdl.io/components/#tables-section
 */
const DataTable = ({
  selectable,
  hover,
  sortable,
  className,
  children,
  ...other
}) => (
  <table
    className={classnames(
      "cdk-data-table",
      {
        "cdk-data-table--selectable": selectable,
        "cdk-data-table--hover": hover,
        "cdk-data-table--sortable": sortable
      },
      className
    )}
    {...other}
  >
    {children}
  </table>
);

DataTable.propTypes = {
  /** True to enable support for selectable rows. */
  selectable: bool,
  /** True to alter row color when hovered over with the mouse. */
  hover: bool,
  /** Enable support for sortable columns. */
  sortable: bool
};

DataTable.defaultProps = {
  hover: true
};

export default DataTable;
