import React from "react";
import classnames from "classnames";
import { bool, oneOf } from "prop-types";

/**
 * Data table cell.
 */
const DataTableCell = ({
  type = "td",
  nonNumeric,
  className,
  children,
  ...other
}) => {
  const T = type;
  return (
    <T
      className={classnames(
        { "cdk-data-table__cell--non-numeric": nonNumeric },
        className
      )}
      {...other}
    >
      {children}
    </T>
  );
};

DataTableCell.propTypes = {
  /** The element type */
  type: oneOf(["td", "th"]),
  /** Enable when displaying non-numeric data */
  nonNumeric: bool
};

export default DataTableCell;
