import React from "react";
import classnames from "classnames";
import DataTableCell from "./DataTableCell";
import { Icon } from "@cdk-uip/react-icon";
import { bool, oneOf } from "prop-types";

/**
 * Data table header cell.
 */
const DataTableHeaderCell = ({
  sortable,
  sortDirection,
  className,
  children,
  ...other
}) => (
  <DataTableCell
    type="th"
    className={classnames(
      {
        "cdk-data-table__column-sort": sortable && sortDirection !== "none",
        [`cdk-data-table__column-sort--${sortDirection}`]:
          sortable && sortDirection !== "none"
      },
      className
    )}
    {...other}
  >
    {sortable && <Icon>arrow_upward</Icon>}
    {children}
  </DataTableCell>
);

DataTableHeaderCell.propTypes = {
  /** Make the column sortable */
  sortable: bool,
  /** The sort direction */
  sortDirection: oneOf(["none", "asc", "desc"])
};

export default DataTableHeaderCell;
