import React from "react";
import classnames from "classnames";

/**
 * Use as a container for the following pagination components (usually
 * in this order):
 *
 * - `DataTablePaginationSlect`
 * - `DataTablePaginationText`
 * - `DataTablePaginationActions`
 * - `DataTablePaginationAction`
 */
const DataTablePagination = ({ className, children, ...other }) => (
  <div className={classnames("cdk-data-table-pagination", className)}>
    {children}
  </div>
);

export default DataTablePagination;
