import React from "react";
import classnames from "classnames";

/**
 * The main pagination text. Usually indicates the current
 * rows and total count (e.g. "1-5 of 20")
 */
const DataTablePaginationText = ({ className, children, ...other }) => (
  <div className={classnames("cdk-data-table-pagination__text", className)}>
    {children}
  </div>
);

export default DataTablePaginationText;
