import React from "react";
import classnames from "classnames";

/**
 * Data table row.
 */
const DataTableRow = ({ selected, className, children, ...other }) => (
  <tr
    className={
      classnames({ "cdk-data-table__row--selected": selected }, className) ||
      undefined
    }
    {...other}
  >
    {children}
  </tr>
);

export default DataTableRow;
