import React from "react";
import classnames from "classnames";
import { Fluid } from "@cdk-uip/react-fluid";

const DataTableToolbar = ({ className, children, ...other }) => (
  <Fluid
    className={classnames("cdk-data-table__toolbar", className)}
    valign="middle"
    wrap="nowrap"
    {...other}
  >
    {children}
  </Fluid>
);

export default DataTableToolbar;
