import React, { Component, Fragment } from "react";
import classnames from "classnames";

export default class DatePickerInput extends Component {
  state = {
    focused: false,
    date: null,
    active: false
  };

  handleFocus = () => {
    this.setState({ focused: true });
  };

  handleBlur = () => {
    this.setState({ focused: false });
  };

  render() {
    const { className, disabled, label, value } = this.props;
    return (
      <Fragment>
        <label
          className={classnames(
            "cdk-date-picker",
            "mdc-text-field",
            "mdc-text-field--upgraded",
            {
              "mdc-text-field--focused": this.state.focused,
              "mdc-text-field--disabled": disabled
            },
            className
          )}
        >
          <input
            type="text"
            className="mdc-text-field__input"
            onFocus={this.handleFocus}
            onBlur={this.handleBlur}
            onClick={this.props.onClick}
            value={value}
            readOnly
          />

          <span
            className={classnames("mdc-floating-label", {
              "mdc-floating-label--float-above": value
            })}
          >
            {label}
          </span>

          <div
            className={classnames("mdc-line-ripple", {
              "mdc-line-ripple--active": this.state.focused
            })}
          />
        </label>
      </Fragment>
    );
  }
}
