import React from "react";
import TestUtils from "react-dom/test-utils";
import renderer from "react-test-renderer";
import DatePickerInput from "./DatePickerInput";

describe("DatePickerInput component", () => {
  it("renders", () => {
    expect(renderer.create(<DatePickerInput />)).toMatchSnapshot();
  });

  it("renders onclick", () => {
    const toggleCalendar = jest.fn();
    const wrapper = TestUtils.renderIntoDocument(
      <DatePickerInput onClick={toggleCalendar} />
    );
    const input = TestUtils.findRenderedDOMComponentWithTag(wrapper, "input");
    TestUtils.Simulate.click(input);
    expect(toggleCalendar).toHaveBeenCalled();
  });
});
