import React, { Fragment } from "react";
import {
  Dialog,
  DialogHeader,
  DialogBody,
  DialogFooter,
  DialogFooterButton
} from ".";
import { Button } from "@cdk-uip/react-button";
import { storiesOf } from "@storybook/react";
import { action } from "@storybook/addon-actions";

storiesOf("dialog", module)
  .add("toggle", () => {
    class Toggle extends React.Component {
      state = { open: false };

      onOpen = () => {
        this.setState({ open: true });
      };

      onAccept = () => {
        this.setState({ open: false });
        action()("onAccept");
      };

      onCancel = () => {
        this.setState({ open: false });
        action()("onCancel");
      };

      render() {
        return (
          <div>
            <Button onClick={this.onOpen}>Show dialog</Button>

            <Dialog
              open={this.state.open}
              onAccept={this.onAccept}
              onCancel={this.onCancel}
            >
              <DialogHeader>Use Google's location service?</DialogHeader>
              <DialogBody>
                Let Google help apps determine location. This means sending
                anonymous location data to Google, even when no apps are
                running.
              </DialogBody>
              <DialogFooter>
                <DialogFooterButton cancel>
                  <span>Cancel</span>
                </DialogFooterButton>
                <DialogFooterButton accept>Accept</DialogFooterButton>
              </DialogFooter>
            </Dialog>
          </div>
        );
      }
    }
    return <Toggle />;
  })
  .add("initially open", () => {
    class Wrapper extends React.Component {
      state = { open: true };
      render() {
        return (
          <Fragment>
            <Button onClick={() => this.setState({ open: true })}>
              Show dialog
            </Button>
            <Dialog open={this.state.open}>
              <DialogBody>Something went wrong!</DialogBody>
              <DialogFooter>
                <DialogFooterButton
                  action
                  onClick={() => this.setState({ open: false })}
                >
                  OK
                </DialogFooterButton>
              </DialogFooter>
            </Dialog>
          </Fragment>
        );
      }
    }
    return <Wrapper />;
  })
