import React from "react";
import { bool } from "prop-types";
import classnames from "classnames";

/**
 * Used as a container for the `Dialog` content.
 * Can contain scrollable content.
 */
const DialogBody = ({ className, scrollable, fullHeight, children }) => (
  <section
    className={classnames(
      "mdc-dialog__body",
      {
        "mdc-dialog__body--scrollable": scrollable,
        "mdc-dialog__body--cdk-full-height": fullHeight
      },
      className
    )}
  >
    {children}
  </section>
);

DialogBody.propTypes = {
  /** Make the body scrollable */
  scrollable: bool
};

export default DialogBody;
