import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

/**
 * Divider component. When used on their own they will render using
 * an `hr` element. When used with a `List` they will render as a
 * `li` element.
 */
const Divider = ({ className, element, inset, ...other }) => {
  const T = element;
  return (
    <T
      className={classnames(
        "mdc-list-divider",
        { "mdc-list-divider--inset": inset },
        className
      )}
      {...other}
    />
  );
};

Divider.propTypes = {
  /** The element to render (hr, li, etc.) */
  element: PropTypes.string,
  /** Inset divider */
  inset: PropTypes.bool
};

Divider.defaultProps = {
  element: "hr"
};

export default Divider;
