import React from "react";
import renderer from "react-test-renderer";
import { Divider } from ".";

it("renders", () => {
  expect(renderer.create(<Divider />)).toMatchSnapshot();
});

it("renders inset", () => {
  expect(renderer.create(<Divider inset />)).toMatchSnapshot();
});

it("renders using a custom element", () => {
  expect(renderer.create(<Divider element="li" />)).toMatchSnapshot();
});
