import React from "react";
import {
  PermanentDrawer,
  DrawerList,
  DrawerListItem,
  TemporaryDrawer,
  TemporaryDrawerHeader,
  TemporaryDrawerHeaderContent,
  TemporaryDrawerContent
} from ".";
import { ListItemGraphic } from "@cdk-uip/react-list";
import { Icon } from "@cdk-uip/react-icon";
import {
  Toolbar,
  ToolbarRow,
  ToolbarSection,
  ToolbarIcon,
  ToolbarTitle
} from "@cdk-uip/react-toolbar";
import { Divider } from "@cdk-uip/react-divider";
import { Headline3 } from "@cdk-uip/react-typography";
import { LayoutGrid, LayoutGridCell } from "@cdk-uip/react-layout-grid";
import { Button } from "@cdk-uip/react-button";
import { storiesOf } from "@storybook/react";
import { action } from "@storybook/addon-actions";
import image from "./16-9.jpg";

storiesOf("drawer", module)
  .add("permanent below toolbar", () => (
    <div>
      <Toolbar>
        <ToolbarRow>
          <ToolbarSection align="start">
            <ToolbarIcon menu />
            <ToolbarTitle>Toolbar</ToolbarTitle>
          </ToolbarSection>
        </ToolbarRow>
      </Toolbar>

      <div style={{ display: "flex" }}>
        <PermanentDrawer>
          <DrawerList>
            <DrawerListItem selected>
              <ListItemGraphic>
                <Icon>inbox</Icon>
              </ListItemGraphic>Inbox
            </DrawerListItem>
            <DrawerListItem>
              <ListItemGraphic>
                <Icon>star</Icon>
              </ListItemGraphic>Star
            </DrawerListItem>
            <DrawerListItem>
              <ListItemGraphic>
                <Icon>send</Icon>
              </ListItemGraphic>
              Sent Mail
            </DrawerListItem>
            <Divider />
            <DrawerListItem>
              <ListItemGraphic>
                <Icon>email</Icon>
              </ListItemGraphic>
              All Mail
            </DrawerListItem>
            <DrawerListItem>
              <ListItemGraphic>
                <Icon>delete</Icon>
              </ListItemGraphic>
              Trash
            </DrawerListItem>
          </DrawerList>
        </PermanentDrawer>

        <LayoutGrid align="left">
          <LayoutGridCell span={12}>
            <Headline3 style={{ margin: 0 }}>Main content</Headline3>
          </LayoutGridCell>
        </LayoutGrid>
      </div>
    </div>
  ))
  .add("temporary", () => {
    class Wrapper extends React.Component {
      state = { open: false };

      render() {
        return (
          <div>
            <Button onClick={() => this.setState({ open: true })}>
              Open drawer
            </Button>

            <TemporaryDrawer
              open={this.state.open}
              onClose={() => this.setState(() => ({ open: false }))}
            >
              <TemporaryDrawerHeader>
                <TemporaryDrawerHeaderContent
                  style={{
                    backgroundImage: `url(${image})`,
                    backgroundSize: "cover",
                    color: "#fff"
                  }}
                >
                  Header content
                </TemporaryDrawerHeaderContent>
              </TemporaryDrawerHeader>
              <TemporaryDrawerContent>
                <DrawerList>
                  <DrawerListItem
                    selected
                    href="#"
                    onClick={e => {
                      e.preventDefault();
                      action()("Inbox");
                    }}
                  >
                    <ListItemGraphic>
                      <Icon>inbox</Icon>
                    </ListItemGraphic>
                    Inbox
                  </DrawerListItem>
                  <DrawerListItem>
                    <ListItemGraphic>
                      <Icon>star</Icon>
                    </ListItemGraphic>
                    Star
                  </DrawerListItem>
                  <DrawerListItem>
                    <ListItemGraphic>
                      <Icon>send</Icon>
                    </ListItemGraphic>
                    Sent Mail
                  </DrawerListItem>
                  <Divider />
                  <DrawerListItem>
                    <ListItemGraphic>
                      <Icon>email</Icon>
                    </ListItemGraphic>
                    All Mail
                  </DrawerListItem>
                  <DrawerListItem>
                    <ListItemGraphic>
                      <Icon>delete</Icon>
                    </ListItemGraphic>
                    Trash
                  </DrawerListItem>
                </DrawerList>
              </TemporaryDrawerContent>
            </TemporaryDrawer>
          </div>
        );
      }
    }
    return <Wrapper />;
  });
