import React from "react";
import { PermanentDrawer, DrawerList, DrawerListItem } from ".";
import renderer from "react-test-renderer";

it("renders below", () => {
  expect(
    renderer.create(
      <PermanentDrawer>
        <DrawerList>
          <DrawerListItem selected>Inbox</DrawerListItem>
          <DrawerListItem>Star</DrawerListItem>
        </DrawerList>
      </PermanentDrawer>
    )
  ).toMatchSnapshot();
});

it("renders above", () => {
  expect(
    renderer.create(
      <PermanentDrawer aboveToolbar>
        <DrawerList>
          <DrawerListItem selected>Inbox</DrawerListItem>
          <DrawerListItem>Star</DrawerListItem>
        </DrawerList>
      </PermanentDrawer>
    )
  ).toMatchSnapshot();
});
