import React from "react";
import classnames from "classnames";
import { number, bool } from "prop-types";

/**
 * Elevate content.
 */
const Elevation = ({ className, z, transition, children, ...other }) => (
  <div
    className={classnames(
      `mdc-elevation--z${z}`,
      { "mdc-elevation-transition": transition },
      className
    )}
    {...other}
  >
    {children}
  </div>
);

Elevation.propTypes = {
  /** Elevation in pixels. Maximum: 24 */
  z: number,
  /** Use a transition animation when the elevation changes */
  transition: bool
};

Elevation.defaultProps = {
  z: 2
};

export default Elevation;
