import React from "react";
import { Elevation } from ".";
import { storiesOf } from "@storybook/react";
import { withKnobs, number } from "@storybook/addon-knobs";

storiesOf("elevation", module)
  .addDecorator(withKnobs)
  .add("props", () => {
    const z = number("z", 2, { range: true, min: 0, max: 24, step: 1 });
    return (
      <Elevation z={z}>
        <p style={{ padding: 16 }}>Elevation: {z}</p>
      </Elevation>
    );
  })
  .add("transition", () => {
    class ControlledElevation extends React.Component {
      state = { z: 2 };

      render() {
        return (
          <Elevation
            z={this.state.z}
            transition
            onMouseEnter={() => this.setState({ z: 24 })}
            onMouseLeave={() => this.setState({ z: 2 })}
            style={{ margin: 24 }}
          >
            <p style={{ padding: 16 }}>
              Hover over me for a transition animation.
            </p>
          </Elevation>
        );
      }
    }
    return <ControlledElevation />;
  });
