import React from "react";
import { Elevation } from ".";
import renderer from "react-test-renderer";

it("renders with default elevation", () => {
  expect(renderer.create(<Elevation>Elevated!</Elevation>)).toMatchSnapshot();
});

it("renders with 4dp elevation", () => {
  expect(
    renderer.create(<Elevation z={4}>Elevated!</Elevation>)
  ).toMatchSnapshot();
});

it("renders with a transition", () => {
  expect(
    renderer.create(
      <Elevation z={4} transition>
        Elevated!
      </Elevation>
    )
  ).toMatchSnapshot();
});
