import React from "react";
import classnames from "classnames";
import { Headline5, Subtitle1 } from "@cdk-uip/react-typography";
import { node } from "prop-types";

EmptyArea.propTypes = {
  /** Title */
  title: node,
  /** Description */
  description: node,
  /** Optional area content */
  children: node
};

export default function EmptyArea({
  className,
  title,
  description,
  children,
  ...other
}) {
  return (
    <div className={classnames("cdk-empty-area", className)} {...other}>
      {title && <Headline5>{title}</Headline5>}
      {description && <Subtitle1>{description}</Subtitle1>}
      {children}
    </div>
  );
}
