import React from "react";
import { EmptyArea } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(<EmptyArea title="Title" description="description" />)
  ).toMatchSnapshot();
});

it("renders with content", () => {
  expect(
    renderer.create(
      <EmptyArea title="Title" description="description">
        <p>Content</p>
      </EmptyArea>
    )
  ).toMatchSnapshot();
});

it("renders with no description", () => {
  expect(renderer.create(<EmptyArea title="Title" />)).toMatchSnapshot();
});

it("renders with no title", () => {
  expect(
    renderer.create(<EmptyArea description="description" />)
  ).toMatchSnapshot();
});
