import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * Used to create a panel within an `<ExpansionPanelGroup>`.
 *
 * This is a controlled component and the application is responsible
 * for maintining the `expanded` state.
 */
const ExpansionPanel = ({ className, expanded, ...other }) => (
  <div
    className={classnames(
      "cdk-expansion-panel",
      { "cdk-expansion-panel--expanded": expanded },
      className
    )}
    {...other}
  />
);

ExpansionPanel.propTypes = {
  /** Render the panel in the expanded state */
  expanded: bool
};

export default ExpansionPanel;
