import React from "react";
import classnames from "classnames";

/**
 * Used as a container for expansion panel actions, usually `<Button>`
 * components.
 */
const ExpansionPanelContentActions = ({ className, children, ...other }) => (
  <div className={classnames("content-actionbar", className)} {...other}>
    {children}
  </div>
);

export default ExpansionPanelContentActions;
