import React from "react";
import classnames from "classnames";

/**
 * Use in a `<ExpansionPanelHeader>` to display secondary text. You may include
 * more than one.
 */
const ExpansionPanelHeaderDescription = ({ className, children, ...other }) => (
  <span className={classnames("header-description", className)} {...other}>
    {children}
  </span>
);

export default ExpansionPanelHeaderDescription;
