import React from "react";
import classnames from "classnames";
import { Icon } from "@cdk-uip/react-icon";

/**
 * The expanded/collapsed icon displayed in an expansion panel header.
 */
const ExpansionPanelHeaderIndicator = ({ className, ...other }) => (
  <span
    className={classnames("header-expansion-indicator", className)}
    {...other}
  >
    <Icon>expand_more</Icon>
  </span>
);

export default ExpansionPanelHeaderIndicator;
