import React from "react";
import classnames from "classnames";

/**
 * Use in conjunction with `<ExpansionPanelHeaderTitle>` to display a subtitle.
 */
const ExpansionPanelHeaderSubtitle = ({ className, children, ...other }) => (
  <span className={classnames("header-subtitle", className)} {...other}>
    {children}
  </span>
);

export default ExpansionPanelHeaderSubtitle;
