import React from "react";
import classnames from "classnames";

/**
 * Use within a `<ExpansionPanelHeader>` to display the panel heading.
 */
const ExpansionPanelHeaderTitle = ({ className, children, ...other }) => (
  <div className={classnames("header-title", className)} {...other}>
    {children}
  </div>
);

export default ExpansionPanelHeaderTitle;
